import { createSlice } from "@reduxjs/toolkit";

const userSlice = createSlice({
  name: "user",
  initialState: undefined,
  reducers: {
    login(state, action) {
      state = action.payload;
    },
  },
});

export const { login } = userSlice;

export default userSlice.reducer;
