import "./button.css";

interface ButtonProps {
  buttonValue: string;
  id: string;
  isDisabled?: boolean;
  onClick: () => void;
  onChange: () => void;
}

const Button = ({
  buttonValue,
  id,
  isDisabled,
  onClick,
  onChange,
}: ButtonProps) => {
  const handleChange = (event: any) => {
    const value = event.target.value;
    console.log("lalala", value);
  };

  const handleClick = (event: any) => {
    onClick();
  };

  // const bttnDisabled = (event: any) => {
  //   if(isDisabled) {
  //     const button = document.getElementById(id);
  //     button?.setAttribute('disabled', '');
  //   }
  // }

  return (
    <>
      <input
        className="customButton"
        type="button"
        id={id}
        disabled={isDisabled}
        value={buttonValue}
        onClick={handleClick}
        onChange={handleChange}
      />
    </>
  );
};

export default Button;
