import { SubmitHandler, useForm } from "react-hook-form";
import "./register.css";

type FormFields = {
  username: string;
  password: string;
  email: string;
};

const Register = () => {
  const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
  } = useForm<FormFields>();

  const onSubmit: SubmitHandler<FormFields> = async (data) => {
    await new Promise((resolve) => setTimeout(resolve, 1000));
    console.log(data);
  };

  return (
    <form className="mainContainer" onSubmit={handleSubmit(onSubmit)}>
      <div className="titleContainer">
        <h2>Register to continue</h2>
      </div>

      <div className={`input-field-${errors.username ? "not-empty" : "empty"}`}>
        <input
          {...register("username", {
            required: "Username is required",
          })}
          type="text"
        />
        <div className="placeholder">Username</div>
        {errors.username && (
          <div className="red-text">{errors.username.message}</div>
        )}
      </div>

      <div className={`input-field-${errors.password ? "not-empty" : "empty"}`}>
        <input
          {...register("password", {
            required: "Password is required",
            minLength: {
              value: 8,
              message: "Password must have 8 characters",
            },
          })}
          type="password"
        />
        <div className="placeholder">Password</div>
        {errors.password && (
          <div className="red-text">{errors.password.message}</div>
        )}
      </div>

      <div className={`input-field-${errors.email ? "not-empty" : "empty"}`}>
        <input
          {...register("email", {
            required: "Email is required",
            validate: (value) => {
              if (!value.includes("@")) {
                return "Email must include @";
              }
              return true;
            },
          })}
          type="email"
        />
        <div className="placeholder">Email</div>
        {errors.email && <div className="red-text">{errors.email.message}</div>}
      </div>

      <button disabled={isSubmitting} type="submit">
        {isSubmitting ? "Loading..." : "Register"}
      </button>
    </form>
  );
};

export default Register;
